import { useState } from "react";



export default function SignUpContent() {
    const [email,setEmail] = useState("")
    return (
    <div className="signup-content">
        <h1>Stay updated!</h1>
        <p>Join 60,00+ product managers receiving monthly updates on:</p>
        <ul>
            <li>Product discovery and build what matters</li>
            <li>Measuring to ensure updates are a success</li>
            <li>
                And much more!
            </li>
        </ul>
        <form className="input-signup">
        <label>Email address</label>
        <input type="text" value={email} onChange={(e)=>setEmail(e.value)} placeholder="some.email@gmail.com" />
        <input type="submit" value="Subscribe to monthly newsletter"/>
        </form>
    </div>
    );
}
